);



CREATE TABLE TLEGSEC_LAWSUIT_STATUS (
	ID SERIAL PRIMARY KEY NOT NULL,
	LAWSUIT_ID INTEGER NOT NULL,  -- n/mm/yyyy  
	STATUS VARCHAR(2) NOT NULL,  --RE=REPORTED, SU=SUE, AM=AMNESTY, CO=CONVICTED
