CREATE TABLE TFIN_CONTRACTOR 
(
	ID SERIAL PRIMARY KEY NOT NULL,	
	
	COMOPANY_NAME VARCHAR(100) NOT NULL,
	FISCAL_NUMBER VARCHAR(50) NOT NULL,
	BUSINESS_NUMBER VARCHAR(50) NOT NULL,
	
	ATTACHMENT BYTEA,
	ADDRESS VARCHAR(100),
	
