	PAYMENT_BALANCE DOUBLE PRECISION,
	CREATED_BY INTEGER,
	CREATED_TIME TIMESTAMP,
	FOREIGN KEY (CREATED_BY) REFERENCES THRM_EMPLOYEE(ID),
