	FOREIGN KEY (STATUS_ID) REFERENCES TFRM_ACTIVITY_STATUS(ID),
	FOREIGN KEY (AUTH_PERSON_ID) REFERENCES THRM_EMPLOYEE(ID)
	
);
*/

