  CONSTRAINT alertrule_ad_language_un UNIQUE (alertrule_id, ad_language),
  FOREIGN KEY (createdby) REFERENCES tperson(id),
  FOREIGN KEY (updatedby) REFERENCES tperson(id)    
)
WITH (
  OIDS=FALSE
);

