-- DROP VIEW vgis_stand_active;

CREATE OR REPLACE VIEW vgis_stand_active AS 
 SELECT s.id,
    s.stand_id,
    s.stand_nr,
    s.mngm_class,
    s.stand_type,
    s.basal_area,
    s.the_geom,
    t.domtext AS mngm_name_al
   FROM ( SELECT tgis_stand.id,
            tgis_stand.stand_id,
            tgis_stand.stand_nr,
            tgis_stand.mngm_class,
            tgis_stand.stand_type,
            tgis_stand.basal_area,
